import React, { Component } from "react"
import moment from "moment"
import TextField from "@material-ui/core/TextField"

class SeasonSearch extends Component {
  constructor() {
    super()
    this.state = {
      loading: false,
      visible: false,
    }
  }

  componentDidMount() {
    this.setState({ loading: true })
    fetch(`https://www.omdbapi.com/?t=chuck&Season=1&apikey=982d108e`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          loading: false,
          season: data.Season,
          episode: data.Episodes[0].Episode,
          title: data.Episodes[0].Title,
          airDate: data.Episodes[0].Released,
        })
        console.log(data)
      })
  }

  render() {
    let date = this.state.airDate
    let coolDate = moment(date).format("MM/DD/YYYY")
    const text = this.state.loading ? "loading..." : this.state.character
    return (
      <div style={{ color: "white" }}>
        <h1>Search Seasons</h1>
        <form onSubmit={this.handleSubmit}>
          <TextField
            value={this.state.input}
            onChange={this.onChange}
            id="filled-full-width"
            label="TV Show"
            style={{ width: "50%", marginTop: "15px" }}
            placeholder="Season"
            InputLabelProps={{
              shrink: true,
            }}
            variant="filled"
          />
        </form>
        <h1>Season {this.state.season}</h1>
        <p>Episode {this.state.episode}</p>
        <p>Title: {this.state.title}</p>
        <p>Air Date: {coolDate}</p>
      </div>
    )
  }
}

export default SeasonSearch
