import React from "react"

class Johnny extends React.Component {
  constructor() {
    super()
    this.state = {
      visible: false,
      johnny: "",
    }
  }
  handleClick = () => {
    this.setState({
      johnny: "He's right here!!!!",
    })
  }

  render() {
    return (
      <div>
        <h1>Where the heck is my Johnny?</h1>
        <button onClick={this.handleClick}>Click to find out</button>
        <h1>{this.state.johnny}</h1>
      </div>
    )
  }
}

export default Johnny
