import React from "react"
import TextField from "@material-ui/core/TextField"

class SeasonSearch extends React.Component {
  constructor() {
    super()
    this.state = {
      visible: false,
      items: [],
    }
  }

  onChange = event => {
    this.setState({ input: event.target.value })
  }
  handleSubmit = event => {
    event.preventDefault()
    const url = `https://www.omdbapi.com/?t=${this.state.input}&Season=1&apikey=982d108e`
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({
          input: "",
          errorMessage: "",
          visible: true,
          items: data,
          //   episode: data.Episodes[0].Episode,
          //   title: data.Episodes[0].Title,
          //   released: data.Episodes[0].Released,
          //   season: data.Season,
        })
        console.log(data)
      })
      .catch(e => {
        this.setState({
          errorMessage:
            "Sorry, nothing found under that name. Please try again.",
        })
      })
  }

  render() {
    let showInformation = !this.state.visible ? (
      <div>
        <h1>Search Seasons Below</h1>
        <form onSubmit={this.handleSubmit}>
          <TextField
            value={this.state.input}
            onChange={this.onChange}
            id="filled-full-width"
            label="Season"
            style={{ width: "50%", marginTop: "15px" }}
            placeholder="Number"
            InputLabelProps={{
              shrink: true,
            }}
            variant="filled"
          />
        </form>
      </div>
    ) : (
      <div>
        <div>
          <h1>Search Seasons Below</h1>
          <form onSubmit={this.handleSubmit}>
            <TextField
              value={this.state.input}
              onChange={this.onChange}
              id="filled-full-width"
              label="Season"
              style={{ width: "50%", marginTop: "15px" }}
              placeholder="Number"
              InputLabelProps={{
                shrink: true,
              }}
              variant="filled"
            />
          </form>
        </div>
        <div>
          <div style={{ marginTop: "10px" }}>
            <h1>Season: {this.state.season}</h1>
          </div>
          <div style={{ marginTop: "10px" }}>
            <b>
              Episode {this.state.episode}: {this.state.title}
            </b>
          </div>
          <div style={{ marginTop: "10px" }}>
            <b>Air Date: {this.state.released} </b>
          </div>
        </div>
      </div>
    )
    let notFound = this.state.errorMessage ? (
      <h3 style={{ color: "red" }}>{this.state.errorMessage}</h3>
    ) : null

    return (
      <div>
        <div style={{ color: "white", marginBottom: "25px" }}>
          {notFound}

          {showInformation}
        </div>
        <div>
          <h1>Test</h1>
        </div>
      </div>
    )
  }
}

export default SeasonSearch
