import React, { Component } from "react"
import TextField from "@material-ui/core/TextField"

class SeasonSearch extends Component {
  constructor() {
    super()
    this.state = {
      loading: false,
      visible: false,
    }
  }

  componentDidMount() {
    this.setState({ loading: true })
    fetch(`https://www.omdbapi.com/?t=ozark&Season=1&apikey=982d108e`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          loading: false,
          episodes: data.Episodes.Title,
          season: data.Season,
          episode: data.Episodes[0].Episode,
        })
        console.log(data)
      })
  }

  render() {
    const text = this.state.loading ? "loading..." : this.state.character
    return (
      <div style={{ color: "white" }}>
        <h1>Search Seasons</h1>
        <form onSubmit={this.handleSubmit}>
          <TextField
            value={this.state.input}
            onChange={this.onChange}
            id="filled-full-width"
            label="TV Show"
            style={{ width: "50%", marginTop: "15px" }}
            placeholder="Season"
            InputLabelProps={{
              shrink: true,
            }}
            variant="filled"
          />
        </form>
        <h1>Season {this.state.season}</h1>
        <p>Episode</p>
      </div>
    )
  }
}

export default SeasonSearch
