import React from "react"

class MovieSearch extends React.Component {
  constructor() {
    super()
    this.state = {
      loading: false,
      character: {},
    }
  }

  onChange = event => {
    this.setState({ input: event.target.value })
  }
  handleSubmit = event => {
    event.preventDefault()
    const url = `https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${this.state.input}`
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({
          input: "",
          errorMessage: "",
          visible: true,
          drinkName: data.drinks[0].strDrink,
          instructions: data.drinks[0].strInstructions,
          recommendedGlass: data.drinks[0].strGlass,
          picture: data.drinks[0].strDrinkThumb + "/preview",
        })
      })
      .catch(e => {
        this.setState({
          errorMessage:
            "Sorry, nothing found under that name. Please try again.",
        })
      })
  }

  render() {
    const text = this.state.loading ? "loading..." : this.state.character.name
    return (
      <div>
        <p>Person: {text}</p>
        <p>Gender: {this.state.gender}</p>
        <p>Birth Year: {this.state.birthyear}</p>
      </div>
    )
  }
}

export default MovieSearch
