import React, { Component } from "react"

class SeasonSearch extends Component {
  constructor() {
    super()
    this.state = {
      loading: false,
      character: {},
    }
  }

  componentDidMount() {
    this.setState({ loading: true })
    fetch(`https://www.omdbapi.com/?t=ozark&Season=1&apikey=982d108e`)
      .then(response => response.json())
      .then(data => {
        this.setState({
          loading: false,
          character: data,
        })
        console.log(data)
      })
  }

  render() {
    const text = this.state.loading ? "loading..." : this.state.character
    return (
      <div>
        <p>{text}</p>
      </div>
    )
  }
}

export default SeasonSearch
