import { Link } from "react-router-dom"
import MovieSearch from "./MovieSearch"
import React from "react"
import PropTypes from "prop-types"
import { makeStyles } from "@material-ui/core/styles"
import AppBar from "@material-ui/core/AppBar"
import Tabs from "@material-ui/core/Tabs"
import Tab from "@material-ui/core/Tab"
import Typography from "@material-ui/core/Typography"
import Box from "@material-ui/core/Box"
import TelevisionSearch from "./TelevisionSearch"

function TabPanel(props) {
  const { children, value, index, ...other } = props

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}>
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  )
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  }
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}))

export default function SimpleTabs() {
  const classes = useStyles()
  const [value, setValue] = React.useState(0)

  const handleChange = (event, newValue) => {
    setValue(newValue)
  }

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="simple tabs example">
          <Link to="/movie" className="nav-link">
            <Tab label="Search Movies" {...a11yProps(0)} />
          </Link>
          <Link to="/tv" className="nav-link">
            <Tab label="Search TV" {...a11yProps(1)} />
          </Link>
          <Tab label="Item Three" {...a11yProps(2)} />
        </Tabs>
      </AppBar>
      <Link to="/movie" className="nav-link">
        <TabPanel value={MovieSearch} index={0}>
          Search Movies
        </TabPanel>
      </Link>
      <Link toto="/tv" className="nav-link">
        <TabPanel value={TelevisionSearch} index={1}>
          Search TV
        </TabPanel>
      </Link>

      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>
    </div>
  )
}

