import React from "react"
import TextField from "@material-ui/core/TextField"

class MovieSearch extends React.Component {
  constructor() {
    super()
    this.state = {
      visible: false,
    }
  }

  onChange = event => {
    this.setState({ input: event.target.value })
  }
  handleSubmit = event => {
    event.preventDefault()
    const url = `https://www.omdbapi.com/?t=${this.state.input}&apikey=982d108e`
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({
          input: "",
          errorMessage: "",
          visible: true,
          title: data.Title,
          year: data.Year,
          plot: data.Plot,
          poster: data.Poster,
          rating: data.imdbRating,
          rated: data.Rated,
          cast: data.Actors,
        })
        console.log(data)
      })
      .catch(e => {
        this.setState({
          errorMessage:
            "Sorry, nothing found under that name. Please try again.",
        })
      })
  }

  render() {
    let poster = this.state.poster
    let showInformation = !this.state.visible ? (
      <div>
        <h1>Search TV Below</h1>
        <form onSubmit={this.handleSubmit}>
          <TextField
            value={this.state.input}
            onChange={this.onChange}
            id="filled-full-width"
            label="TV Show"
            style={{ width: "50%", marginTop: "15px" }}
            placeholder="Title"
            InputLabelProps={{
              shrink: true,
            }}
            variant="filled"
          />
        </form>
      </div>
    ) : (
      <div>
        <div>
          <h1>Search Movies Below</h1>
          <form onSubmit={this.handleSubmit}>
            <TextField
              value={this.state.input}
              onChange={this.onChange}
              id="filled-full-width"
              label="TV Show"
              style={{ width: "50%", marginTop: "15px" }}
              placeholder="Title"
              InputLabelProps={{
                shrink: true,
              }}
              variant="filled"
            />
          </form>
        </div>
        <div>
          <img src={poster} style={{ marginTop: "15px" }}></img>
          <div>Title: {this.state.title}</div>
          <div>Year: {this.state.year}</div>
          <div>Rated: {this.state.rated}</div>
          <div>Cast: {this.state.cast}</div>
          <div>IMDb Rating: {this.state.rating}</div>
          <div>Plot: {this.state.plot}</div>
          <div>Episodes: {this.state.episodes}</div>
          <div>Total Seasons:{this.state.totalSeasons} </div>
        </div>
      </div>
    )
    console.log(poster)
    return <div>{showInformation}</div>
  }
}

export default MovieSearch
